import React from 'react';
import {Tabbar, TabbarItem, Panel, PanelHeader, View, Epic} from '@vkontakte/vkui';
import '@vkontakte/vkui/dist/vkui.css';
import Icon28Newsfeed from '@vkontakte/icons/dist/28/newsfeed';
import Icon28PictureOutline from '@vkontakte/icons/dist/28/picture_outline';
import Main from './Main'

class App extends React.Component {
	constructor (props) {
		super(props);

		this.state = {
		  activeStory: 'Main'
		};
		this.onStoryChange = this.onStoryChange.bind(this);
	  }

	  onStoryChange (e) {
		this.setState({ activeStory: e.currentTarget.dataset.story })
	  }

    render() {
        return (
            <Epic activeStory={this.state.activeStory} tabbar={
				<Tabbar>
				  <TabbarItem
					onClick={this.onStoryChange}
					selected={this.state.activeStory === 'Main'}
					data-story="Main"
					text="Новости"
				  ><Icon28Newsfeed /></TabbarItem>
				  <TabbarItem
					onClick={this.onStoryChange}
					selected={this.state.activeStory === 'series'}
					data-story="series"
					text="Сериалы"
				  ><Icon28PictureOutline /></TabbarItem>
				</Tabbar>
			  }>

				<View activePanel="Main" id="Main">
                <Panel id="mainPanel">
                    <PanelHeader>Example</PanelHeader>
                    <Group>
                        <Div>
                            Hello world
                        </Div>
                    </Group>
                </Panel>
            </View>

				<View id="series" activePanel="Series">
				  <Panel id="series">
					<PanelHeader>Сериалы</PanelHeader>
				  </Panel>
				</View>
			  </Epic>
        );
    }
}

export default App;
