import React from 'react';
import {Tabbar, TabbarItem, Panel, PanelHeader, View, Epic} from '@vkontakte/vkui';
import '@vkontakte/vkui/dist/vkui.css';

class App extends React.Component {
	constructor (props) {
		super(props);

		this.state = {
		  activeStory: 'more'
		};
		this.onStoryChange = this.onStoryChange.bind(this);
	  }

	  onStoryChange (e) {
		this.setState({ activeStory: e.currentTarget.dataset.story })
	  }

    render() {
        return (
            <Epic activeStory={this.state.activeStory} tabbar={
				<Tabbar>
				  <TabbarItem
					onClick={this.onStoryChange}
					selected={this.state.activeStory === 'main'}
					data-story="main"
					text="Новости"
				  ><Icon28NewsfeedOutline /></TabbarItem>
				  <TabbarItem
					onClick={this.onStoryChange}
					selected={this.state.activeStory === 'series'}
					data-story="series"
					text="Сериалы"
				  ><Icon28SearchOutline /></TabbarItem>
				</Tabbar>
			  }>
				<View id="main" activePanel="Main">
				  <Panel id="main">
					<PanelHeader>Новости</PanelHeader>
				  </Panel>
				</View>
				<View id="series" activePanel="Series">
				  <Panel id="series">
					<PanelHeader>Сериалы</PanelHeader>
				  </Panel>
				</View>
			  </Epic>
        );
    }
}

export default App;
